let timer, slideLine = document.querySelector(".slider__list"), slides = document.querySelectorAll(".slider__item"), sliderWidth = document.querySelector(".slider").clientWidth, count = 0; function nextSlide() { ++count >= slides.length && (count = 0), shiftSlider(), autoPlay() } function prevSlide() { --count < 0 && (count = slides.length - 1), shiftSlider(), autoPlay() } function shiftSlider() { slideLine.style.transform = "translate(-" + count * sliderWidth + "px)" } function autoPlay() { timer = setTimeout(nextSlide, 4e3) } autoPlay(), document.querySelector(".slider__arrow-right").addEventListener("click", function () { nextSlide(), clearTimeout(timer) }), document.querySelector(".slider__arrow-left").addEventListener("click", function () { prevSlide(), clearTimeout(timer) });